.h-wrapper {
  background: center / cover no-repeat;
  background-image: url(https://placeimg.com/1000/1000/tech);
  background-color: rgba(10, 10, 10, .8);
  background-blend-mode: multiply;
}
header {
  text-align: center;
  color: white;
}
nav ul {
  display: flex;
  flex-direction: column;
  list-style: none;
  justify-content: center;
  font-family: var(--display-font);
  font-weight: 600;
  text-transform: uppercase;
  font-size: 0.8125rem;
  padding: 0;
}
nav li {
  padding: .5rem;
}
address {
  font-family: var(--display-font);
  font-weight: 400;
  font-style: normal;
  font-size: 1rem;
}

@media (min-width: 768px) {
  header {
    height: 67.625rem;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    max-width: 1920px;
    margin: 0 auto;
  }
  nav ul {
    flex-direction: row;
  }
  nav {
    border-bottom: 4px solid rgba(255,255,255,0.1);
  }
  .title {
    align-self: center;
  }
  .title p {
    font-size: 1.375rem;
    margin: 2.625rem 0;
  }
  address {
    display: flex;
    justify-content: space-between;
    margin: 3.375rem 4.75rem;
  }
}

.ul {
  list-style: none;
  justify-content: center;
  font-family: var(--display-font);
  font-weight: 600;
  text-transform: uppercase;
  font-size: 0.75rem;
  letter-spacing: 0.15rem;
  padding-bottom: 1rem;
}
.ul a:active {
  color: var(--brand-color);
}
@media (min-width: 768px) {
  .ul {
    display: flex;
  }
  .gallery {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: repeat(2, 50%);
    max-width: 1920px;
    max-height: 960px;
    margin: 0 auto;
  }
  .tallpic {
    grid-row-start: 1;
    grid-row-end: span 2;
  }
}

.team {
  text-align: center;
}
figure {
  margin: 0;
}
figcaption {
  background-color: rgba(101, 52, 255, 1);
  height: 26.375rem;
  width: 26.375rem;
  /* Purple boxes are styled but currently hidden */
  display: none;
  /* display: flex; */
  flex-direction: column;
  justify-content: center;
  color: white;
  font-size: 1rem;
}
.teamname {
  font-family: var(--display-font);
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.2rem;
}
@media (min-width: 768px) {
  .teampics {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    max-width: 1920px;
    max-height: 580px;
    margin: 0 auto;
  }
}

.t-wrapper {
  background-color: #F7F7F7;
}
.testimonials {
  margin: 0 auto;
}
blockquote {
  margin: 0;
  font-size: 1.125rem;
  line-height: 2.25rem;
}
.quotename {
  text-transform: uppercase;
  font-family: var(--display-font);
  font-weight: 600;
}
.quotetitle {
  font-style: italic;
}
@media (min-width: 768px) {
  .quotesblock {
    display: grid;
    grid: auto-flow / 1fr 1fr;
    gap: 2rem;
    width: 60%;
    margin: 0 auto;
    color: var(--dark-color);
    max-width: 1920px;
    margin: 0 auto;
  }
  .quotesblock figure {
    background: padding-box no-repeat url("img/noun_quotation_1903069_1.svg");
    padding: 4rem 4.5rem;
    margin: 0 auto;
  }
}

.cta {
  text-align: center;
  background-color: var(--brand-color);
  height: 15.8125rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
}
.h2 {
  color: white;
  margin-bottom: 1.25rem;
}
.button-link {
  background-color: white;
  color: var(--verydarkgray);
}

