  --text-font: 'Lora', serif;
  font-family: var(--text-font);
  font-size: 1rem;
  margin: 0 auto;
  background-color: white;
}

.h1, .h2, .h3 {
  font-family: var(--display-font);
}
.h1 {
  font-weight: 700;
  color: white;
/*   font-size: 4.375rem; */
  font-size: clamp(2.5rem, 5vw, 4.375rem);
}
.h2 {
  font-weight: 700;
  color: var(--darkgray);
/*   font-size: 2.5rem; */
  font-size: clamp(1.75rem, 3vw, 2.5rem);
}
.h3 {
  font-weight: 600;
  color: var(--darkgray);
  font-size: 1.25rem;
}
.button-link {
  text-transform: uppercase;
  background-color: white;
  color: var(--verydarkgray);
  font-family: var(--display-font);
  font-weight: 700;
  font-size: 1rem;
  letter-spacing: 0.2625rem;
  margin: 1rem;
  padding: 1rem 2rem;
  border-radius: 2rem;
  box-shadow: 0px 0.625rem 1.25rem rgba(0, 0, 0, 0.2);
}
.button-link:last-child {
  background-color: var(--brand-color);
  color: white;
  box-shadow: 0px 625rem 1.25rem var(--brand-shadow);
}
.img {
  display: block;
  width: 100%;
  height: 100%;
  object-fit: cover;
}
.address {
  font-family: var(--display-font);
  font-weight: 400;
  font-style: normal;
  font-size: 1rem;
}
.intro {
  margin: 0 auto;
  padding: 4rem;
  text-align: center;
  line-height: 2.1875rem;
}
.intro p {
  color: #4B4B4B;
  padding-top: 2rem;
}
@media (min-width: 768px) {
  .intro p {
    font-size: 1.25rem;
  }
}
@media (min-width: 1024px) {
  .intro p {
    width: 60%;
    margin: 0 auto;
  }
}

.visuallyhidden {
  border: 0;
  clip: rect(0 0 0 0);
  height: 1px;
  margin: -1px;
  overflow: hidden;
  padding: 0;
  position: absolute;
  width: 1px;
}

/* Default "copy" styles - need to adjust */
.primo-copy {
  margin: 0 auto;
  width: 100%;
  padding-right: 2rem;
  padding-left: 2rem;
  font-size: 1rem;
  color: #4B4B4B;
  width: 70%;

  h1 { 
  	font-size: 4.375rem;
    font-weight: 700;
    font-family: var(--display-font);
    color: var(--darkgray);
  }

  h2 {
    font-size: 2.5rem;
    font-weight: 700;
    font-family: var(--display-font);
    color: var(--darkgray);
  }

  ol {
    list-style-type: decimal;
    list-style-position: inside;
  }

  ul {
    list-style-type: disc;
    list-style-position: inside;
  }

  p {
    display: inline;
  }

  ol {
    list-style-type: decimal;
    list-style-position: inside;
  }

  a {
    color: #1c64f2;
    text-decoration: underline;
  }

  blockquote {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1),
      0 2px 4px -1px rgba(0, 0, 0, 0.06);
    padding: 1.5rem;
  }

  mark {
    --text-opacity: 1;
    color: #161e2e;
    --bg-opacity: 1;
    background-color: #fce96a;
  }
