    max-width: 768px;
  }
}

@media (min-width: 1024px) {
  .primo-copy {
    max-width: 1024px;
  }
}

@media (min-width: 1280px) {
  .primo-copy {
    max-width: 1280px;
  }
}

@media (min-width: 1536px) {
  .primo-copy {
    max-width: 1536px;
  }
