.h-wrapper {
  background: center / cover no-repeat;
  background-image: url(https://placeimg.com/1000/1000/tech);
  background-color: rgba(10, 10, 10, .8);
  background-blend-mode: multiply;
}
header {
  text-align: center;
  color: white;
}
nav ul {
  display: flex;
  flex-direction: column;
  list-style: none;
  justify-content: center;
  font-family: var(--display-font);
  font-weight: 600;
  text-transform: uppercase;
  font-size: 0.8125rem;
  padding: 0;
}
nav li {
  padding: .5rem;
}
address {
  font-family: var(--display-font);
  font-weight: 400;
  font-style: normal;
  font-size: 1rem;
}

@media (min-width: 768px) {
  header {
    height: 67.625rem;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    max-width: 1920px;
    margin: 0 auto;
  }
  nav ul {
    flex-direction: row;
  }
  nav {
    border-bottom: 4px solid rgba(255,255,255,0.1);
  }
  .title {
    align-self: center;
  }
  .title p {
    font-size: 1.375rem;
    margin: 2.625rem 0;
  }
  address {
    display: flex;
    justify-content: space-between;
    margin: 3.375rem 4.75rem;
  }
}

