.h-wrapper {
  position: relative;
}
.background {
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
}
.background::after {
		content: "";
		position: absolute;
		top: 0;
		left: 0;
		right: 0;
		bottom: 0;
		background-color: rgba(10, 10, 10, .8);
		mix-blend-mode: multiply;
}

/* .h-wrapper {
  background: center / cover no-repeat;
  background-image: url(https://placeimg.com/1000/1000/tech);
  background-color: rgba(10, 10, 10, .8);
  background-blend-mode: multiply;
} */

.text-overlay {
  text-align: center;
  color: white;
  position: relative;
}
ul {
  display: flex;
  flex-direction: column;
  list-style: none;
  justify-content: center;
  font-family: var(--display-font);
  font-weight: 600;
  text-transform: uppercase;
  font-size: 0.8125rem;
  padding: 0;
}
li {
  padding: .5rem;
}
.title {
  margin: 2rem;
}
#links {
  display: flex;
  flex-direction: column;
  justify-content: center;
}
address {
  padding-bottom: 2rem;
}

@media (min-width: 640px) {
  .text-overlay {
    height: 67.625rem;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    max-width: 1920px;
    margin: 0 auto;
  }
  ul {
    flex-direction: row;
  }
  nav {
    border-bottom: 4px solid rgba(255,255,255,0.1);
  }
  .title {
    align-self: center;
  }
  .title p {
    font-size: 1.375rem;
    margin: 2.625rem 0;
  }
  #links {
  display: flex;
  flex-direction: row;
  justify-content: center;
	}
  address {
    display: flex;
    justify-content: space-between;
    margin: 3.375rem 4.75rem;
  }
}

#nav {
  list-style: none;
  justify-content: center;
  font-family: var(--display-font);
  font-weight: 600;
  text-transform: uppercase;
  font-size: 0.75rem;
  letter-spacing: 0.15rem;
  padding-bottom: 1rem;
  text-align: center;
}
@media (min-width: 768px) {
  #nav {
    display: flex;
  }
  #nav li {
    padding: 1rem;
  }
  #gallery {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: repeat(2, 50%);
    max-width: 1920px;
    max-height: 960px;
    margin: 0 auto;
  }
  #tallpic {
    grid-row-start: 1;
    grid-row-end: span 2;
  }
}

.s-wrapper {
  background-color: var(--verydarkgray);
}
.h3 {
  color: white;
  text-transform: uppercase;
  letter-spacing: .25rem;
}
p {
  color: #9A9A9A;
  line-height: 1.875rem;
}
.square {
  width: 3rem;
  height: 3rem;
  background-color: var(--brand-color);
  margin-bottom: 3rem;
}
.services {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));
  gap: 5rem;
  width: 70%;
  margin: 0 auto;
  padding: 7.625rem 2rem;
  max-width: 1920px;
 }

.team {
  text-align: center;
}
figure {
  margin: 0;
}
figcaption {
  background-color: rgba(101, 52, 255, 1);
  height: 26.375rem;
  width: 26.375rem;
  /* Purple boxes are styled but currently hidden */
  display: none;
  /* display: flex; */
  flex-direction: column;
  justify-content: center;
  color: white;
  font-size: 1rem;
}
#name {
  font-family: var(--display-font);
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.2rem;
}
@media (min-width: 768px) {
  .teampics {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    max-width: 1920px;
    max-height: 580px;
    margin: 0 auto;
  }
}

.t-wrapper {
  background-color: #F7F7F7;
}
.testimonials {
  padding: 2rem;
  max-width: 1920px;
}
.quotesblock {
  margin: 0 auto;
  color: var(--dark-color);
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(450px, 1fr));
	gap: 2rem;
  font-size: clamp(.8rem, 1vw, 1.125rem);
}
@media (min-width: 640px) {
  .quotesblock {
  	max-width: 70%;
  }
}
figure {
  padding: 4rem 4.5rem;
  margin: 0 auto;
  background: padding-box no-repeat url(data:image/svg+xml;base64,);
}
blockquote {
  margin: 0;
  line-height: 2.25rem;
  padding-bottom: 2rem;
}
.quotename {
  text-transform: uppercase;
  font-family: var(--display-font);
  font-weight: 600;
}
.quotetitle {
  font-style: italic;
}

.logos {
  display: grid;
  justify-content: center;
  margin: 0 auto;
  padding: 5rem;
  grid-template-columns: repeat( auto-fit, min(4.5rem) );
  gap: 10rem;
  max-width: 1920px;
}
img {
  height: 4.5rem;
}

.c-wrapper {
  text-align: center;
  background-color: var(--brand-color);
  display: flex;
  flex-direction: column;
  justify-content: center;
}
.cta {
  padding-top: 3rem;
  padding-bottom: 5rem;
}
.h2 {
  color: white;
  padding-bottom: 3rem;
}
.button-link {
  background-color: white;
  color: var(--verydarkgray);
}

footer {
  background-color: var(--verydarkgray);
}
#footerblock {
  color: white;
  font-size: 1rem;
  margin: 0 auto;
  padding: 2rem;
  max-width: 1920px;
}
p {
  font-size: 1rem;
}
#summary {
  padding: 1rem;
  display: grid;
  place-items: center;
  line-height: 1.625rem;
  color: #FEFEFE;
}
#footernav {
  text-align: center;
  list-style: none;
  text-transform: uppercase;
  font-family: var(--display-font);
  font-weight: 400;
  font-size: 0.8125rem;
  line-height: 1.75rem;
  padding-bottom: 1rem;
  columns: 2;
}
#footernav li {
  padding: 0;
  margin: 0;
}
address {
  text-align: center;
}
#subscribe {
  padding: 1em;
  font-size: 1.25rem;
  margin: 0;
  color: #FEFEFE;
  text-align: center;
}
#subscribe-text {
  font-size: 1.25rem;
  padding-bottom: 1rem;
}
#emailbox {
  display: flex;
  align-items: stretch;
}
#email {
  flex: 0 1 100%;
  display: block;
  border-width: 0;
  font-size: 1rem;
  padding: 1rem 2rem 1rem 2rem;
  margin: 0;
  border-radius: 2rem 0 0 2rem;
}
#email::placeholder {
  color: #8A8A8A;
  font-family: var(--text-font)
}
#emailbutton {
  flex: 0 0 3em;
  display: block;
  background-color: #6534FF;
  margin: 0;
  border-width: 0;
  border-radius: 0 2rem 2rem 0;
  box-shadow: none;
}
#icon {
  height: 1rem;
  width: 1rem;
  background-color: white;
  margin: 0 auto;
}
.c-wrapper {
  background-color: #1C1C1C;
}
#copyright {
  color: #DEDEDE;
  height: 5rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  width: 70%;
  max-width: 1920px;
  margin: 0 auto;
  font-size: .75rem;
}
@media (min-width: 500px) {
  #subscribe {
    text-align: left;
  }
}
@media (min-width: 590px) {
  #footerblock {
		display: grid;
  }
  #summary, #subscribe {
    grid-column: 1/3;
    justify-self: center;
  }
  #footernav, address {
 		place-self: center;
  }
  address {
  align-self: start;
  margin: 0;
	}
}
@media (min-width: 880px) {
  #footerblock {
    width: 80%;
  }
}
@media (min-width: 1000px) {
  #footerblock {
    grid-template-columns: repeat(4, 1fr);
  }
  #summary {
    grid-column: 1/2;

  }
  #footernav {
    columns: 1;
    padding: 0 1rem;
  }
  address {
    padding: 0 1rem;
    justify-content: flex-start;
    line-height: 2.5;
  }
  #subscribe {
    grid-column: 4/5;
    padding: 0 2rem;
  }
